INSERT [dbo].[tblPatientFeedbacks] ([serviceFeedBackID], [status], [content], [dateFeedback], [rateStar], [patientID], [serviceID]) VALUES (37, 1, N'Có nhiều người bạn đã hỏi mình làm ở đâu mình đã giới thiệu tới đây, hiện bạn mình rất hài lòng.', CAST(N'2022-06-19' AS Date), 5, 40, 23)
