INSERT [dbo].[tblPatientFeedbacks] ([serviceFeedBackID], [status], [content], [dateFeedback], [rateStar], [patientID], [serviceID]) VALUES (38, 1, N'Đội ngũ bác sĩ giỏi, tâm lý mà cơ sở trang thiết bị tại Nha Khoa cũng vô cùng hiện đại và sạch sẽ. ', CAST(N'2022-06-20' AS Date), 5, 42, 6)
