INSERT [dbo].[tblPatientFeedbacks] ([serviceFeedBackID], [status], [content], [dateFeedback], [rateStar], [patientID], [serviceID]) VALUES (42, 1, N'Bác sĩ tay nghề cao. Chăm sóc bệnh nhân nhiệt tình.Cảm ơn phòng khám', CAST(N'2022-06-20' AS Date), 5, 54, 20)
