INSERT [dbo].[tblPatientFeedbacks] ([serviceFeedBackID], [status], [content], [dateFeedback], [rateStar], [patientID], [serviceID]) VALUES (43, 1, N'Bác sĩ và y tá nhiệt tình. Khá hài lòng về dịch vụ và làm việc của đội ngũ nha khoa. Mặc dù có rất nhiều phòng khám nhưng vẫn thích nha khoa của các bạn. Hài lòng đánh giá 5 sao nhé.', CAST(N'2022-06-20' AS Date), 5, 19, 21)
