INSERT [dbo].[tblPatientFeedbacks] ([serviceFeedBackID], [status], [content], [dateFeedback], [rateStar], [patientID], [serviceID]) VALUES (45, 1, N'Tưởng là đau lắm, nhưng không ngờ chả đau tẹo nào. Chưa đầy 5 phút, tạm biệt 2 em răng khôn. Bác sĩ chuyên môn tốt, mát tay quá. Mọi người ai sợ nhổ răng thì cứ về với đội của nha khoa nhé.', CAST(N'2022-06-21' AS Date), 5, 27, 33)
GO
INSERT [dbo].[tblPatientFeedbacks] ([serviceFeedBackID], [status], [content], [dateFeedback], [rateStar], [patientID], [serviceID]) VALUES (48, 1, N'Chân thành cảm ơn đội ngũ bác sĩ của nha khoa và các bạn nhân viên hỗ trợ của phòng khám đã đồng hành cùng mình trong suốt 2 năm qua. VỚi tác phong làm 
việc chuyên nghiệp, nhiệt tình với khách hàng, cơ sở vật chất sang trọng và lịch sự, mình hoàn toàn hài lòng về kết quả hiện tại.', CAST(N'2022-06-22' AS Date), 5, 46, 43)
GO
INSERT [dbo].[tblPatientFeedbacks] ([serviceFeedBackID], [status], [content], [dateFeedback], [rateStar], [patientID], [serviceID]) VALUES (49, 1, N'Tìm đến phòng khám với bao lo lắng hoang mang khi đang niềng dở ở cơ sở khác, thật sự không dám xem lại hình ảnh của mình ở thời điểm đó! 
Sau khi gặp đội ngũ bác sĩ tư vấn nhiệt tình, mình lại đặt niềm tin ở đây 1 lần nữa. Quả không sai! Sau hơn 1 năm gắn bó mình đã có được hàm răng và 
nụ cười như thế này, cuộc sống vui vẻ và tự tin hơn hẳn. Cảm ơn nha khoa rất nhiều.  ', CAST(N'2022-06-22' AS Date), 5, 52, 19)
GO
INSERT [dbo].[tblPatientFeedbacks] ([serviceFeedBackID], [status], [content], [dateFeedback], [rateStar], [patientID], [serviceID]) VALUES (51, 1, N'19 tháng đồng hành cùng nha khoa và đội ngũ y tá của phòng khám là quãng thời gian mình được tư vấn, chăm sóc vô cùng tận tình. Hành trình đi tìm nụ cười đã thành công mỹ mãn
. Lựa chọn niềng răng tại nha khoa là sự lựa chọn hoàn toàn đúng đắn đến giờ phút này.', CAST(N'2022-06-23' AS Date), 5, 55, 41)
GO
INSERT [dbo].[tblPatientFeedbacks] ([serviceFeedBackID], [status], [content], [dateFeedback], [rateStar], [patientID], [serviceID]) VALUES (52, 1, N'Từ nỗi sợ ê buốt trong khi làm, nỗi lo lắng vì răng bị mài nhiều khi bọc sứ, cô đã thay đổi hẳn cách suy nghĩ khi thực hiện dịch vụ tại nha khoa: không hề ê buốt sau khi làm, ăn uống thoải mái, không có cảm giác “giả” sau bọc sứ.
Bác sĩ Hàn Quốc tận tình, nhẹ nhàng trong thăm khám.Phòng khám đạt chuẩn, công nghệ hiện đại.', CAST(N'2022-06-23' AS Date), 5, 57, 22)
GO
INSERT [dbo].[tblPatientFeedbacks] ([serviceFeedBackID], [status], [content], [dateFeedback], [rateStar], [patientID], [serviceID]) VALUES (53, 1, N'Mình niềng trong giai đoạn đang có baby nhưng bác sĩ luôn hỗ trợ mình làm mình rất yên tâm. Còn 2 tháng nữa mình sẽ tháo nhưng hiện tại mình thấy răng minh khá là ok. Cảm ơn bác sĩ và nha khoa rất nhiều ', CAST(N'2022-06-24' AS Date), 5, 59, 43)
