INSERT [dbo].[tblPatientFeedbacks] ([serviceFeedBackID], [status], [content], [dateFeedback], [rateStar], [patientID], [serviceID]) VALUES (55, 1, N'Thật may mắn khi được biết tới sử dụng dịch vụ tại nha khoa, một địa chỉ đáng tin cậy về chỉnh nha tại Việt Nam. Hi vọng thế hệ các con sẽ có những nụ cười đẹp tại đây.', CAST(N'2022-06-28' AS Date), 5, 61, 46)
GO
INSERT [dbo].[tblPatientFeedbacks] ([serviceFeedBackID], [status], [content], [dateFeedback], [rateStar], [patientID], [serviceID]) VALUES (56, 1, N'Tôi thực sự rất hài lòng về sự phục vụ nhiệt tình bác sĩ và đội ngũ nhân viên của nha khoa. Một chiếc răng khôn to dài và chân răng rất cong 
rất khó nhổ. NHưng bác sĩ Đoan đã nhổ nó rất nhanh và không đau đớn. Cảm ơn bac sĩ Đoan và nha khoa rất nhiều. ', CAST(N'2022-06-25' AS Date), 5, 98, 39)
