INSERT [dbo].[tblPatientFeedbacks] ([serviceFeedBackID], [status], [content], [dateFeedback], [rateStar], [patientID], [serviceID]) VALUES (59, 1, N'Bác sĩ tận tình, luôn thăm hỏi động viên khách hàng và chu đáo trong quá trình chăm sóc và điều trị. Hiện nay răng của mình đã được tháo và kết quả làm mình thực sự muốn òa khóc vì hạnh phúc, răng đều đặn và lành lặn như người bình thường, 
Hiện nay mình đã cởi mở hơn trong giao tiếp và cảm nhận thấy đây là món quà mà cuộc sống ban tặng ', CAST(N'2022-06-26' AS Date), 5, 50, 25)
GO
INSERT [dbo].[tblPatientFeedbacks] ([serviceFeedBackID], [status], [content], [dateFeedback], [rateStar], [patientID], [serviceID]) VALUES (60, 1, N'Sau 2 năm ăn chung ngủ chung với mắc cài, cuối cùng em đã được tháo niềng. Bây giờ, em chả sợ nói sợ cười như ngày xưa nữa. Miệng giờ lúc nào cũng toe toét luôn. Trước lúc niềng, em chỉ mong mình bớt hô thôi, bây giờ lại lời được cả mũi, cả cằm nữa. Em vui lắm.', CAST(N'2022-06-27' AS Date), 5, 27, 18)
GO
INSERT [dbo].[tblPatientFeedbacks] ([serviceFeedBackID], [status], [content], [dateFeedback], [rateStar], [patientID], [serviceID]) VALUES (61, 1, N'Mình rất hài lòng về nha khoa. Ở đây dịch vụ và đội ngũ bác sĩ đều rất nhiệt tình và tận tâm với bệnh nhân. Mình đang trong giai đoạn tách kẽ thui nhưng mình tin là bác sĩ ở đây sẽ giúp mình lấy lại hàm răng đều đẹp và nụ cười tươi tắn trở lại. ', CAST(N'2022-06-27' AS Date), 5, 8, 50)
GO
INSERT [dbo].[tblPatientFeedbacks] ([serviceFeedBackID], [status], [content], [dateFeedback], [rateStar], [patientID], [serviceID]) VALUES (62, 1, N'Mình kết chị tư vấn quá, kiểu tư vấn đúng nghĩa chứ k đẩy khách hàng phải chọn cái này cái kia ngay lập tức ý. Xong mình chốt niềng luôn. Vào chụp ảnh, lấy dấu, xong hẹn hôm sau qua niềng. ', CAST(N'2022-06-28' AS Date), 5, 48, 18)
GO
INSERT [dbo].[tblPatientFeedbacks] ([serviceFeedBackID], [status], [content], [dateFeedback], [rateStar], [patientID], [serviceID]) VALUES (63, 1, N'Tui highly recommend cho những ai đang tìm địa chỉ niềng răng uy tín thì có thể tham khảo tại nha khoa này nha. Đội ngũ bác sĩ siêu giỏi mà có tâm, trang thiết bị mới tinh, công nghệ cũng hiện đại. Mà đặc biệt ở đây hỗ trợ nhiều lắm, khuyến mãi cực lớn. May mắn nhất của mình chính là tìm được địa chỉ trung tâm nha khoa uy tín. Có vậy mới có sự xuất hiện của hàm răng đều đẹp ngày hôm nay.”', CAST(N'2022-06-28' AS Date), 5, 49, 24)
GO
INSERT [dbo].[tblPatientFeedbacks] ([serviceFeedBackID], [status], [content], [dateFeedback], [rateStar], [patientID], [serviceID]) VALUES (65, 1, N'Dịch vụ rất tốt, bác sĩ nhẹ nhàng và tư vấn rất rõ ràng và có chuyên môn nên mình rất hài lòng với dịch vụ ở đây cho con gái của mình!', CAST(N'2022-07-26' AS Date), 5, 51, 1)
