INSERT [dbo].[tblPatientFeedbacks] ([serviceFeedBackID], [status], [content], [dateFeedback], [rateStar], [patientID], [serviceID]) VALUES (72, 1, N'Các bác sĩ có chuyên môn tốt, tận tâm. Service chăm sóc khách hàng tốt. Trang thiết bị hiện đại.', CAST(N'2022-07-25' AS Date), 5, 28, 10)
