INSERT [dbo].[tblPatientFeedbacks] ([serviceFeedBackID], [status], [content], [dateFeedback], [rateStar], [patientID], [serviceID]) VALUES (74, 1, N'Quy trình tư vấn của trung tâm rất tốt, lời khuyên của bác sĩ giúp mình hiểu rõ và yên tâm về vấn đề răng miệng của mình.', CAST(N'2022-07-25' AS Date), 5, 19, 13)
