INSERT [dbo].[tblPatientFeedbacks] ([serviceFeedBackID], [status], [content], [dateFeedback], [rateStar], [patientID], [serviceID]) VALUES (90, 1, N'Sau một thời gian chỉnh nha tại DentalCare qua cảm nhận, đội ngũ ở đây làm việc có trách nhiệm , nhiệt tình, luôn quan tâm hỏi thăm tình trạng khách hàng để hỗ trợ kịp thời.Tôi đã có nhiều thay đổi trong quá trình niềng và hài lòng với DentalCare.', CAST(N'2022-07-19' AS Date), 5, 19, 33)
