INSERT [dbo].[tblServices] ([serviceID], [serviceName], [servicePrice], [description], [status], [adminID], [serviceTypeID]) VALUES (1, N'Cạo vôi & đánh bóng', 300000, N'Vôi răng là những mảng bám bị vôi hoá trên bề mặt răng và giữa răng với nướu. Vôi răng quá nhiều đe doạ đến sức khoẻ răng miệng do có thể gây viêm nướu. Do đó cần phải đến nha sĩ để lấy vôi răng và đánh bóng răng định kỳ nhằm loại bỏ các mảng bám này. 
- Cạo vôi răng giúp loại bỏ vôi răng, mảng bám hình thành trong miệng. Hạn chế những bệnh lý về nướu răng như nướu đỏ, nướu bị sưng, chảy máu, hơi thở có mùi, thậm chí là những trường hợp viêm nha chu, mất răng… nếu tình trạng vôi răng bám dày hơn. Phòng ngừa các bệnh lý răng miệng hiệu quả và an toàn. Giảm tình trạng chảy máu chân răng hoặc ê buốt.
- Thông thường thời gian cạo vôi răng khoảng 15 đến 30 phút, làm sạch vôi răng ở chân răng. Trong trường hợp vôi răng bám dày, khách sẽ chịu cảm giác ê buốt hơn một chút nhưng sẽ không giảm chức năng nhai sau khi hoàn thành.
- Cạo vôi đánh bóng răng nên được thực hiện mỗi 4-6 tháng một lần nhằm đảm bảo chân răng luôn sạch sẽ, hàm răng đẹp, sáng bóng và thẩm mỹ. ', 0, 1, 6)
GO
INSERT [dbo].[tblServices] ([serviceID], [serviceName], [servicePrice], [description], [status], [adminID], [serviceTypeID]) VALUES (2, N'Điều trị viêm nha chu', 200000, N'<p><strong>Vi&ecirc;m nha chu</strong> hay c&ograve;n gọi l&agrave; <em><strong>bệnh nha chu </strong></em>l&agrave; t&igrave;nh trạng c&aacute;c tổ chức xung quanh răng bị vi&ecirc;m.</p><p><strong>Vi&ecirc;m nha chu</strong> được chia th&agrave;nh hai nh&oacute;m ch&iacute;nh, đ&oacute; l&agrave; <strong><em>vi&ecirc;m lợi</em></strong> v&agrave; <strong><em>vi&ecirc;m nha chu</em></strong>.</p><p><strong>Bệnh nha chu</strong> l&agrave; một bệnh rất phổ biến của v&ugrave;ng răng miệng, hay gặp ở <strong>lứa tuổi trung ni&ecirc;n, người gi&agrave;</strong> v&agrave; l&agrave; một trong những nguy&ecirc;n nh&acirc;n thường gặp của t&igrave;nh trạng mất răng ở người lớn.</p><p>Sự ph&aacute;t triển của vi khuẩn trong mảng b&aacute;m răng l&agrave; <em><strong>nguy&ecirc;n nh&acirc;n ch&iacute;nh của bệnh.</strong></em></p><p><em><strong>Nếu kh&ocirc;ng đ&aacute;nh răng, vệ sinh răng miệng tốt</strong></em>, mảng b&aacute;m răng sẽ t&iacute;ch tụ v&agrave; dần dần kho&aacute;ng h&oacute;a <em><strong>trở th&agrave;nh cao răng</strong></em> với lượng vi khuẩn ng&agrave;y c&agrave;ng tăng.</p><p><strong>Dấu hiệu nhận biết bệnh nha chu:</strong></p><ul><li><em>- Nướu (lợi) tấy đỏ, sưng v&agrave; dễ bị chảy m&aacute;u (khi đ&aacute;nh răng hoặc l&agrave;m sạch kẽ răng). </em></li><li><em>- Hơi thở c&oacute; m&ugrave;i h&ocirc;i. </em></li><li><em>- Răng bị lung lay hoặc c&aacute;ch xa c&aacute;c răng c&ograve;n lại. </em></li><li><em>- Sự thay đổi về sự kh&iacute;t s&aacute;t nhau của c&aacute;c răng khi cắn.</em></li></ul>', 1, 2, 6)
GO
INSERT [dbo].[tblServices] ([serviceID], [serviceName], [servicePrice], [description], [status], [adminID], [serviceTypeID]) VALUES (3, N'Trám răng sữa', 100000, N'<p><em><strong>C&oacute; n&ecirc;n tr&aacute;m răng sữa của trẻ hay kh&ocirc;ng l&agrave; thắc mắc của nhiều cha mẹ</strong></em></p><p>Bởi <strong><em>răng sữa chỉ l&agrave; răng mọc tạm</em></strong>, sau khi c&aacute;c b&eacute; lớn l&ecirc;n sẽ được thay thế ho&agrave;n to&agrave;n bằng răng vĩnh viễn.</p><p>Tuy nhi&ecirc;n, c&aacute;c b&aacute;c sĩ nha khoa đ&atilde; khuyến c&aacute;o rằng, nếu như răng sữa bị s&acirc;u m&agrave; kh&ocirc;ng được tr&aacute;m kịp thời sẽ <em><strong>ảnh hưởng rất lớn đến sức khỏe của trẻ</strong></em> như: <strong><em>g&acirc;y bệnh h&ocirc;i miệng, đau nhức răng dẫn đến biếng ăn, suy dinh dưỡng..</em></strong></p><p>Hiện nay, <strong><em>phương ph&aacute;p trám răng là kỹ thuật nha khoa</em></strong> được áp dụng trong việc điều trị v&agrave; ph&ograve;ng ngừa bệnh l&yacute; răng miệng ở trẻ nhỏ.</p><p>Kỹ thuật n&agrave;y được thực hiện v&ocirc; c&ugrave;ng <strong><em>đơn giản v&agrave; nhẹ nh&agrave;ng</em></strong>, <em><strong>kh&ocirc;ng g&acirc;y đau nhức g&igrave; cho trẻ.</strong></em></p><p>B&aacute;c sĩ sẽ lấy đi khoảng răng bị s&acirc;u trước v&agrave; d&ugrave;ng vật liệu chuy&ecirc;n dụng để tr&aacute;m v&agrave;o, qu&aacute; tr&igrave;nh n&agrave;y <em><strong>chỉ diễn ra khoảng 15 &ndash; 30 ph&uacute;t </strong></em>l&agrave; ho&agrave;n th&agrave;nh.</p>', 1, 2, 5)
