INSERT [dbo].[tblServices] ([serviceID], [serviceName], [servicePrice], [description], [status], [adminID], [serviceTypeID]) VALUES (8, N'Răng sứ Titan', 2000000, N'<p><strong>Răng sứ l&otilde;i Titan</strong> là m&ocirc;̣t trong những loại răng sứ <em><strong>l&acirc;u đời nhất hiện nay</strong></em>, được nhiều bác sĩ nha khoa và nhi&ecirc;̀u kh&aacute;ch h&agrave;ng tin dùng.</p><p><strong>Về cấu tạo của răng sứ Titan gồm 2 phần:</strong></p><ul><li>ph&acirc;̀n lõi kim loại b&ecirc;n trong ( Titanium)&nbsp;</li><li>b&ecirc;n ngoài được phủ m&ocirc;̣t lớp sứ nguy&ecirc;n chất.</li></ul><p><strong>Titanium</strong> l&agrave; một loại chất liệu để l&agrave;m n&ecirc;n răng sứ titan thẩm mỹ.</p><p><strong>Khung sườn của răng sứ Titan</strong> được l&agrave;m bằng hợp kim chứa từ <strong>4% tới 6% l&agrave; Titanium</strong> v&agrave; được phủ một lớp mỏng men sứ caramco3 tr&ecirc;n bề mặt răng.</p><p><strong>Răng sứ Titan có 3 loại chính như:</strong></p><ul><li>Răng sứ Titan Berlin.</li><li>Răng sứ Titan Margin.</li><li>Răng sứ Titan Vita.</li></ul><p><strong>V&ecirc;̀ bản ch&acirc;́t,</strong> những loại răng sứ Titan này đ&ecirc;̀u <em><strong>gi&ocirc;́ng nhau v&ecirc;̀ c&acirc;́u tạo cũng như ch&acirc;́t lượng.</strong></em></p><p><em><strong>Khác nhau </strong>ở ch&ocirc;̃</em> chúng được sản xu&acirc;́t ở những <strong><em>địa đi&ecirc;̉m hoặc c&ocirc;ng ty kh&ocirc;ng gi&ocirc;́ng nhau</em></strong>.</p><p><strong>ƯU ĐI&Ecirc;̉M RĂNG SỨ KIM LOẠI TITAN</strong></p><ul><li><em>- Nhẹ và khó bị oxy hóa trong m&ocirc;i trường mi&ecirc;̣ng </em></li><li><em>- B&ecirc;̀n chắc, ăn nhai t&ocirc;́t - Lành tính với cơ th&ecirc;̉. </em></li><li><em>- Khả năng tương thích sinh học cao.</em></li></ul>', 1, 2, 1)
