INSERT [dbo].[tblServices] ([serviceID], [serviceName], [servicePrice], [description], [status], [adminID], [serviceTypeID]) VALUES (10, N'Răng sứ Venus', 5500000, N'<p><strong>Răng sứ Venus</strong> l&agrave; loại răng to&agrave;n sứ c&oacute; <strong><em>độ bền ca</em>o</strong>, <strong><em>m&agrave;u sắc tự nhi&ecirc;n như răng thật</em></strong>, thuộc nh&oacute;m răng sứ kh&ocirc;ng kim loại.</p><p>Được thừa hưởng t&iacute;nh chất vượt trội của sườn <em><strong>răng sứ zirconia chịu lực tốt</strong></em>, tương th&iacute;ch v&agrave; ph&ugrave; hợp với con người.</p><p>Với chất liệu si&ecirc;u nhẹ, <strong>Răng sứ Venus</strong> được coi l&agrave; một bước tiến quan trọng trong c&ocirc;ng nghệ l&agrave;m răng thẩm mỹ hiện nay.</p><p><strong>Răng Venus</strong> được sản xuất theo<em><strong> c&ocirc;ng nghệ CAD/CAM hiện đại nhất tr&ecirc;n thế giới</strong></em>&nbsp;hiện nay. N&oacute; được cấu tạo từ <strong>100% ph&ocirc;i vật liệu</strong> c&oacute; xuất xứ từ những tập đo&agrave;n<em><strong> Nha khoa nổi tiếng như Đức, Mỹ, độ cứng 900 Mpa (gấp 5 lần răng kim loại)</strong></em></p><p><strong>ƯU ĐIỂM CỦA RĂNG SỨ VENUS:</strong></p><ul><li><em>- Khả năng ăn nhai v&agrave; chịu lực lớn. </em></li><li><em>- An to&agrave;n, kh&ocirc;ng g&acirc;y k&iacute;ch ứng. </em></li><li><em>- Tuổi thọ của răng to&agrave;n sứ Venus cũng được đ&aacute;nh gi&aacute; cao hơn hẳn so với những d&ograve;ng răng sứ kh&aacute;c. </em></li><li><em>- Răng to&agrave;n sứ Venus c&oacute; tuổi thọ tới 20 năm. </em></li><li><em>- Gi&aacute; răng Venus vẫn rẻ hơn nhiều so với c&aacute;c d&ograve;ng răng to&agrave;n sứ cao cấp kh&aacute;c. V&iacute; dụ như D-Max, E-Max, Ceramil,vv&hellip;</em></li></ul>', 1, 2, 1)
