INSERT [dbo].[tblServices] ([serviceID], [serviceName], [servicePrice], [description], [status], [adminID], [serviceTypeID]) VALUES (11, N'Nhổ răng sữa', 100000, N'<p><strong>Nhổ răng sữa</strong> tại nh&agrave; cho b&eacute; kh&aacute; đơn giản, nhưng nếu bố mẹ cần thực hiện thao t&aacute;c một c&aacute;ch dứt kho&aacute;t, trọn vẹn để kh&ocirc;ng g&acirc;y ra tổn thương.</p><p>Một số biến chứng thường gặp khi trẻ nhổ răng tại nh&agrave; l&agrave; <strong>vi&ecirc;m nha chu.</strong></p><p><em><strong>Nguy&ecirc;n nh&acirc;n</strong></em> dẫn đến bệnh l&yacute; n&agrave;y l&agrave; do khi nhổ răng kh&ocirc;ng đảm bảo vệ sinh, nhổ răng c&ograve;n s&oacute;t ch&acirc;n răng.</p><p><em><strong>T&igrave;nh trạng nghi&ecirc;m trọng hơn</strong></em> c&ograve;n g&acirc;y ra biến chứng nhiễm tr&ugrave;ng, &aacute;p xe răng lan rộng.</p><p><strong>Thời điểm th&iacute;ch hợp để nhổ răng sữa cho b&eacute;?</strong></p><ul><li><em>- Một số trường hợp răng sữa kh&ocirc;ng thể tự rụng m&agrave; phải chủ động nhổ răng sữa.</em></li><li><em>- L&uacute;c n&agrave;y, x&aacute;c định đ&uacute;ng thời điểm để nhổ răng sữa cho trẻ l&agrave; rất quan trọng.</em></li><li><em>- Đối với c&aacute;c răng kh&ocirc;ng lung lay th&igrave; thời điểm nhổ răng tốt nhất cho trẻ l&agrave; khi răng vĩnh viễn bắt đầu trồi l&ecirc;n. </em></li><li><em>- Nếu nhổ răng đ&uacute;ng thời điểm n&agrave;y sẽ gi&uacute;p răng vĩnh viễn mọc l&ecirc;n đ&uacute;ng tại vị tr&iacute; răng sữa vừa nhổ, tr&aacute;nh t&igrave;nh trạng răng lệch lạc.</em></li></ul>', 1, 2, 4)
