INSERT [dbo].[tblServices] ([serviceID], [serviceName], [servicePrice], [description], [status], [adminID], [serviceTypeID]) VALUES (14, N'Tẩy trắng răng tại nha khoa', 1500000, N'<p><strong>Tẩy trắng răng tại ph&ograve;ng kh&aacute;m nha:</strong></p><p>Liệu tr&igrave;nh l&agrave;m trắng răng tại ph&ograve;ng nha chuy&ecirc;n nghiệp <em><strong>bao gồm 3 bước:</strong></em></p><ul><li>cạo v&ocirc;i,</li><li>đ&aacute;nh b&oacute;ng</li><li>tẩy trắng răng.</li></ul><p>C&aacute;c b&aacute;c sĩ kết hợp giữa thuốc c&oacute; nồng độ tẩy trắng cao <strong><em>35 &ndash; 37%</em></strong> v&agrave; <strong><em>ch&ugrave;m &aacute;nh s&aacute;ng với cường độ mạnh</em></strong> để k&iacute;ch hoạt thuốc ngấm s&acirc;u hơn.</p><p>Trước khi sử dụng thuốc tẩy trắng, b&aacute;c sĩ đeo dụng cụ bảo vệ m&ocirc;i, nướu, khoang miệng; đồng thời b&ocirc;i th&ecirc;m thuốc chống &ecirc; buốt để kh&aacute;ch h&agrave;ng cảm thấy thoải m&aacute;i.</p><p><strong>Đặc điểm nổi trội</strong> của phương ph&aacute;p n&agrave;y l&agrave; thực hiện<em><strong> nhanh ch&oacute;ng, c&oacute; t&aacute;c dụng ngay</strong></em> cả trường hợp nhiễm m&agrave;u nặng, m&agrave;u v&agrave;ng sậm, nhiễm m&agrave;u do Tetracycline, Fluorosis, răng h&agrave;m tr&ecirc;n v&agrave; h&agrave;m dưới được tẩy trắng c&ugrave;ng l&uacute;c, cho cả <em><strong>hai h&agrave;m răng trắng đều tự nhi&ecirc;n.</strong></em></p><p><strong>Tẩy trắng răng c&oacute; hại kh&ocirc;ng?</strong></p><ul><li><em>Tẩy trắng răng được nghi&ecirc;n cứu l&agrave; an to&agrave;n cho sức khỏe răng miệng, </em></li><li><em>Kh&ocirc;ng l&agrave;m hại men răng, kh&ocirc;ng l&agrave;m thay đổi cấu tr&uacute;c răng nếu thực hiện đ&uacute;ng c&aacute;ch. </em></li><li><em>Để đảm bảo an to&agrave;n v&agrave; đạt kết quả tối ưu, bạn n&ecirc;n t&igrave;m đến nha khoa sử dụng thuốc tẩy trắng ch&iacute;nh h&atilde;ng, hệ thống đ&egrave;n tẩy trắng hiện đại.</em></li></ul>', 1, 2, 7)
GO
INSERT [dbo].[tblServices] ([serviceID], [serviceName], [servicePrice], [description], [status], [adminID], [serviceTypeID]) VALUES (15, N'Tẩy trắng tại nhà ', 900000, N'<p><strong>Đeo m&aacute;ng tẩy trắng răng tại nh&agrave;:</strong></p><p>B&aacute;c sĩ sẽ lấy dấu răng v&agrave; l&agrave;m m&aacute;ng tẩy vừa vặn, đ&uacute;ng với k&iacute;ch thước răng của mỗi người.</p><p>Loại m&aacute;ng n&agrave;y được l&agrave;m <strong><em>bằng nhựa plastic trong suốt, an to&agrave;n</em></strong>, <strong><em>kh&ocirc;ng g&acirc;y tổn thương đến nướu.</em></strong></p><p>N&oacute; c&oacute; t&aacute;c dụng giữ thuốc tẩy, ngăn kh&ocirc;ng cho nước bọt tr&agrave;n v&agrave;o ảnh hưởng qu&aacute; tr&igrave;nh tẩy trắng răng. B&aacute;c sĩ sẽ cung cấp thuốc c&oacute; nồng độ tẩy trắng từ <strong><em>10 &ndash; 15%</em></strong> v&agrave; hướng dẫn chi tiết c&aacute;ch sử dụng m&aacute;ng tẩy v&agrave; xử l&yacute; những t&igrave;nh huống c&oacute; thể xảy ra.</p><p><strong>Ưu điểm của phương ph&aacute;p n&agrave;y l&agrave;</strong></p><ul><li><em>gi&uacute;p bạn chủ động thời gian (đeo m&aacute;ng từ 2 &ndash; 3 giờ bất cứ khi n&agrave;o),</em></li><li><em>thực hiện ngay tại nh&agrave; với chi ph&iacute; thấp.</em></li></ul><p>Thế nhưng, n&oacute; kh&ocirc;ng đạt hiệu quả tẩy trắng với tất cả trường hợp, <strong><em>chỉ được &aacute;p dụng cho c&aacute;c trường hợp nhiễm m&agrave;u nhẹ như:</em></strong></p><ul><li><em>nhiễm m&agrave;u ngoại lai do t&aacute;c động b&ecirc;n ngo&agrave;i,</em></li><li><em>răng nhiễm m&agrave;u do tuổi t&aacute;c.</em></li></ul>', 1, 2, 7)
GO
INSERT [dbo].[tblServices] ([serviceID], [serviceName], [servicePrice], [description], [status], [adminID], [serviceTypeID]) VALUES (16, N'Đính đá kim cương thẩm mỹ', 700000, N'<p><strong>Đ&iacute;nh đ&aacute; kim cương l&ecirc;n răng</strong> l&agrave; một thủ thuật Nha Khoa nhằm <strong><em>tạo sự kết nối l&agrave;m cho đ&aacute; kim cương d&iacute;nh chặt v&agrave;o răng</em></strong> m&agrave; kh&ocirc;ng bị sứt ra khi ăn nhai nhờ v&agrave;o c&ocirc;ng nghệ đ&egrave;n chiếu v&agrave; keo Nha Khoa đặc biệt.</p><ul><li><em>- Đ&aacute; kim cương được gắn cố định v&agrave; bền chặt.</em></li><li><em>- Chỉ c&oacute; thể lấy ra khi tới Nha Khoa. </em></li><li><em>- Sau khi lấy đ&aacute; kim cương ra, c&aacute;c lỗ nhỏ sẽ được tr&aacute;m lại bằng phương ph&aacute;p tr&aacute;m răng composite chuy&ecirc;n dụng ho&agrave;n to&agrave;n kh&ocirc;ng g&acirc;y mất thẩm mỹ.</em></li></ul><p>C&oacute; thể mua tại Nha Khoa hoặc c&aacute;c cửa h&agrave;ng đ&aacute; qu&yacute;, v&agrave;ng bạc.</p><p>Đ&aacute; kim cương đ&iacute;nh l&ecirc;n răng thường l&agrave; loại cỡ nhỏ khoảng 1-2,5 ly, c&oacute; gi&aacute; từ 600,000 đến 900,000đ/ vi&ecirc;n.</p><p>Nghe c&oacute; vẻ đơn giản nhưng để đảm bảo được độ bền của đ&aacute; cũng như <em><strong>kh&ocirc;ng l&agrave;m mất vệ sinh</strong></em>, <strong><em>ảnh hưởng đến sức khỏe răng miệng</em></strong> th&igrave; b&aacute;c sĩ phải c&oacute; những kiến thức về c&ocirc;ng nghệ n&agrave;y cũng như sự tỉ mỉ kh&eacute;o tay của b&aacute;c sĩ.</p><p><em>Tổng thời gian</em> để đ&iacute;nh đ&aacute; l&ecirc;n răng chỉ mất tầm <strong><em>10-15 ph&uacute;t</em></strong> v&agrave; kh&ocirc;ng hề g&acirc;y cảm gi&aacute;c đau đớn nh&eacute;!</p>', 1, 2, 7)
GO
INSERT [dbo].[tblServices] ([serviceID], [serviceName], [servicePrice], [description], [status], [adminID], [serviceTypeID]) VALUES (17, N'Mắc cài kim loại', 30000000, N'<p><em><strong>Mắc c&agrave;i kim loại</strong></em> l&agrave; dụng cụ được sử dụng để <em><strong>điều trị chỉnh răng.</strong></em></p><p><strong>Mắc c&agrave;i kim loại</strong> được nha sĩ gắn v&agrave;o răng của bệnh nh&acirc;n, kết hợp kỹ thuật trong chỉnh nha để d&ugrave;ng lực tạo ra từ mắc c&agrave;i v&agrave; d&acirc;y cung, gi&uacute;p di chuyển răng đến vị tr&iacute; mong muốn.</p><p>Niềng răng mắc c&agrave;i kim loại l&agrave; <em><strong>phương ph&aacute;p truyền thống.</strong></em></p><p>Nha sĩ c&oacute; thể chỉ cần điều <strong>chỉnh mắc c&agrave;i sau 6 tuần</strong> thay v&igrave; 3 hoặc 4 tuần so với c&aacute;c kỹ thuật th&ocirc;ng thường, đồng thời việc c&acirc;n bằng lực cũng tốt hơn, gi&uacute;p thời gian chỉnh răng <strong>giảm từ 30 -50%.</strong></p><p><strong>Ưu điểm Mắc c&agrave;i kim loại:</strong></p><ul><li><em>Chi ph&iacute; chỉnh nha thấp, </em></li><li><em>hiệu quả cao, </em></li><li><em>thực hiện được những ca chỉnh nha kh&oacute;, </em></li><li><em>thời gian ngắn.</em></li></ul><p><strong>Khi n&agrave;o n&ecirc;n niềng răng mắc c&agrave;i kim loại:</strong></p><ul><li><em>- Trường hợp răng m&oacute;m. </em></li><li><em>- Trường hợp răng h&ocirc;. </em></li><li><em>- Trường hợp răng khấp khểnh. </em></li><li><em>- Trường hợp răng thưa.</em></li></ul>', 1, 2, 2)
GO
INSERT [dbo].[tblServices] ([serviceID], [serviceName], [servicePrice], [description], [status], [adminID], [serviceTypeID]) VALUES (18, N'Mắc cài sứ', 35000000, N'<p><strong>Niềng răng mắc c&agrave;i sứ</strong> l&agrave; phương ph&aacute;p chỉnh nha &aacute;p dụng kh&iacute; cụ chỉnh nha tương tự niềng răng mắc c&agrave;i kim loại,</p><p><strong>Điểm kh&aacute;c biệt </strong>l&agrave; mắc c&agrave;i kim loại được thay bằng <strong><em>mắc c&agrave;i chất liệu sứ.</em></strong></p><p><em><strong>Niềng răng mắc c&agrave;i</strong></em> sứ bao gồm:</p><ul><li><em>Mắc c&agrave;i chất liệu sứ gắn cố định tr&ecirc;n bề mặt răng,</em></li><li><em>D&acirc;y cung nằm trong r&atilde;nh mắc c&agrave;i.</em></li></ul><p><em><strong>Mắc c&agrave;i sứ</strong></em> c&oacute; m&agrave;u trắng trong <em><strong>rất giống răng thật </strong></em>n&ecirc;n sẽ &iacute;t bị lộ niềng hơn phương ph&aacute;p niềng răng mắc c&agrave;i truyền thống.</p><p><strong>Đối tượng n&agrave;o c&oacute; thể niềng răng mắc c&agrave;i sứ ?</strong></p><ul><li><em>Niềng răng mắc c&agrave;i sứ rất th&iacute;ch hợp cho người c&oacute; răng mọc sai vị tr&iacute;, răng ch&igrave;a ra ngo&agrave;i hoặc ch&igrave;a v&agrave;o trong, khoảng c&aacute;ch giữa c&aacute;c răng xa,&hellip;</em></li><li><em>B&ecirc;n cạnh đ&oacute;, c&aacute;c trường hợp sai lệch khớp cắn, h&agrave;m hẹp, h&agrave;m lệch cũng rất ph&ugrave; hợp với phương ph&aacute;p n&agrave;y.</em></li></ul><p><strong>Ưu điểm Mắc c&agrave;i sứ</strong> :</p><ul><li><em>- Đảm bảo hiệu quả niềng răng </em></li><li><em>- T&iacute;nh thẩm mỹ cao khi niềng </em></li><li><em>- An to&agrave;n với người niềng Th&ocirc;ng thường, thời gian niềng răng mắc c&agrave;i sứ sẽ được thực hiện trong 18-24 th&aacute;ng.</em></li></ul>', 1, 2, 2)
GO
INSERT [dbo].[tblServices] ([serviceID], [serviceName], [servicePrice], [description], [status], [adminID], [serviceTypeID]) VALUES (19, N'Niềng răng trong suốt invisalign', 40000000, N'<p><strong><em>Niềng răng trong suốt Invisalign</em></strong> l&agrave; phương ph&aacute;p niềng răng <em><strong>kh&ocirc;ng d&ugrave;ng đến mắc c&agrave;i</strong></em>, d&acirc;y thun như niềng răng th&ocirc;ng thường m&agrave;<strong><em> sử dụng c&aacute;c</em></strong> <strong><em>khay niềng trong suốt để chỉnh răng.</em></strong></p><p><em><strong>Niềng răng Invisalign</strong></em> bao gồm một chuỗi c&aacute;c khay niềng trong suốt được thiết kế cho từng c&aacute; nh&acirc;n dựa theo h&agrave;m răng của từng người v&agrave; được sử dụng lần lượt ở từng giai đoạn kh&aacute;c nhau trong suốt qu&aacute; tr&igrave;nh chỉnh nha.</p><p>Bệnh nh&acirc;n cần mang tổng cộng khoảng từ <strong><em>20 &ndash; 40 khay Invisalign</em></strong> t&ugrave;y v&agrave;o mức độ sai lệch răng.</p><p>Để <strong>Invisalign</strong> đạt được hoạt động tối ưu nhất, người d&ugrave;ng cần phải mang khay Invisalign <strong><em>tối thiểu l&agrave; 22 giờ/ng&agrave;y.</em></strong></p><p><strong>Ưu điểm&nbsp;Niềng răng trong suốt Invisalign:</strong></p><ul><li><em>Thẩm mỹ, dễ d&agrave;ng th&aacute;o lắp để vệ sinh ăn uống tạo cảm gi&aacute;c thoải m&aacute;i dễ chịu,</em></li><li><em>Sử dụng c&ocirc;ng nghệ ti&ecirc;n tiến ph&ugrave; hợp cho từng bệnh nh&acirc;n, r&uacute;t ngắn thời gian đi kh&aacute;m tại ph&ograve;ng kh&aacute;m nha khoa.</em></li><li><em>Kh&aacute;ch h&agrave;ng c&oacute; thể biết trước kết quả. Th&ocirc;ng thường việc điều trị niềng răng invisalign ở người lớn k&eacute;o d&agrave;i từ 1-2 năm.</em></li></ul>', 1, 2, 2)
GO
INSERT [dbo].[tblServices] ([serviceID], [serviceName], [servicePrice], [description], [status], [adminID], [serviceTypeID]) VALUES (20, N'Đặt chốt cùi giả kim loại ', 400000, N'<p><strong>C&ugrave;i răng giả kim loại</strong> hay c&ograve;n gọi l&agrave; <strong><em>c&ugrave;i răng giả</em></strong> ti&ecirc;u chuẩn được l&agrave;m bằng chất liệu kim loại. Hợp chất kim loại kh&ocirc;ng gỉ từ <em><strong>crom &ndash; titan &ndash; coban hoặc niken &ndash; crom &ndash; titan.</strong></em></p><p>C&ugrave;i giả kim loại ổn định tương đối với răng v&agrave; nướu.</p><p><strong>Ưu điểm của loại c&ugrave;i kim loại l&agrave;</strong></p><ul><li><em>Chi ph&iacute; thấp, khả năng chịu lực tốt. </em></li><li><em>Tuy nhi&ecirc;n c&ugrave;i giả kim loại lại k&eacute;m về mặt thẩm mỹ, m&agrave;u sắc kim loại kh&ocirc;ng được đẹp v&agrave; tự nhi&ecirc;n.</em></li><li><em>Do đ&oacute; c&ugrave;i răng giả kim loại chỉ th&iacute;ch hợp với c&aacute;c vị tr&iacute; răng h&agrave;m b&ecirc;n trong.</em></li><li><em>Tuổi thọ của c&ugrave;i răng kim loại từ 5 đến 7 năm.</em></li></ul><p><strong>GẮN C&Ugrave;I RĂNG GIẢ C&Oacute; ĐAU KH&Ocirc;NG?</strong></p><ul><li><em>Tạo c&ugrave;i giả l&agrave; một kỹ thuật đơn giản, kh&ocirc;ng qu&aacute; phức tạp, kh&ocirc;ng x&acirc;m lấn nhiều đến m&ocirc; nướu v&agrave; răng, do đ&oacute; bệnh nh&acirc;n kh&ocirc;ng cần phải qu&aacute; lo lắng.</em></li><li><em>Qu&aacute; tr&igrave;nh gắn c&ugrave;i giả được thiết lập tr&igrave;nh tự, đảm bảo an to&agrave;n v&agrave; kh&ocirc;ng g&acirc;y đau cho kh&aacute;ch h&agrave;ng. </em></li><li><em>Tuy c&oacute; độ cứng chắc cao, nhưng c&ugrave;i giả kim loại kh&ocirc;ng thẩm mỹ bằng c&ugrave;i to&agrave;n sứ, thường bị đen sau một thời gian sử dụng.</em></li></ul>', 1, 2, 5)
GO
INSERT [dbo].[tblServices] ([serviceID], [serviceName], [servicePrice], [description], [status], [adminID], [serviceTypeID]) VALUES (21, N'Đặt chốt cùi giả sứ ', 1000000, N'<p><strong>C&ugrave;i giả to&agrave;n sứ:</strong>&nbsp;l&agrave; dạng m&ocirc; phỏng trụ răng thật được m&agrave;i sẵn <em><strong>(bằng sứ nguy&ecirc;n chất)</strong></em> <strong><em>để bọc răng sứ.</em></strong></p><p>Loại c&ugrave;i n&agrave;y c&oacute; đặc t&iacute;nh tự nhi&ecirc;n như trụ răng thật, <em><strong>gi&uacute;p phục h&igrave;nh răng</strong></em> trong trường hợp kh&aacute;ch h&agrave;ng bị g&atilde;y vỡ răng lớn.</p><p><strong>Ưu điểm:</strong></p><ul><li><em>C&ugrave;i giả to&agrave;n sứ đang l&agrave; lựa chọn tối ưu d&agrave;nh cho kh&aacute;ch h&agrave;ng muốn bọc răng sứ thẩm mỹ, m&agrave; cấu tr&uacute;c răng chưa được đảm bảo.</em></li><li><em>C&oacute; khả năng th&iacute;ch ứng với m&ocirc;i trường khoang miệng rất tốt v&igrave; l&agrave;m từ sứ 100%. </em></li><li><em>Kh&ocirc;ng g&acirc;y k&iacute;ch ứng, đảm bảo an to&agrave;n cho kh&aacute;ch h&agrave;ng sử dụng. </em></li><li><em>C&ugrave;i giả to&agrave;n sứ c&oacute; khả năng lưu giữ m&atilde;o sứ tốt v&agrave; đạt t&iacute;nh thẩm mỹ rất cao.</em></li><li><em>C&ugrave;i giả được gắn trước khi bọc răng sứ khi: </em></li><li><em>Răng bị mất phần lớn m&ocirc; răng, cần đặt chốt v&agrave; t&aacute;i tạo lại c&ugrave;i.</em></li><li><em>Răng bị mất phần lớn m&ocirc; răng, c&oacute; chỉ định bọc m&atilde;o răng.</em></li></ul><p><strong>Một số trường hợp kh&ocirc;ng thể &aacute;p dụng l&agrave;m c&ugrave;i giả l&agrave;:</strong></p><ul><li><em>Răng mất qu&aacute; lớn, m&ocirc; răng v&agrave; ch&acirc;n răng qu&aacute; yếu</em></li></ul>', 1, 2, 5)
GO
INSERT [dbo].[tblServices] ([serviceID], [serviceName], [servicePrice], [description], [status], [adminID], [serviceTypeID]) VALUES (22, N'Răng sứ Zolid', 4000000, N'<p><em><strong>Răng sứ Zirconia</strong></em> l&agrave; loại răng sứ được l&agrave;m từ <em><strong>100% chất liệu Zirconia.</strong></em></p><p>N&oacute; được đ&aacute;nh gi&aacute; l&agrave; một trong những loại răng sứ <em><strong>c&oacute; chất lượng</strong></em> v&agrave; <em><strong>h&igrave;nh thức đẹp nhất ở thời điểm hiện tại.</strong></em></p><p>Sản phẩm răng sứ n&agrave;y được <em><strong>chia th&agrave;nh hai loại </strong></em>l&agrave;<em><strong> Răng sứ Zirconia to&agrave;n khố</strong></em><strong>i</strong> v&agrave; <em><strong>Răng sứ Katana Zirconia.</strong></em></p><p><strong>Răng to&agrave;n sứ Zolid</strong> được ứng dụng để phục h&igrave;nh cho nhiều trường hợp kh&aacute;c nhau như:</p><ul><li>Răng sứt mẻ, g&atilde;y vỡ, răng c&oacute; h&igrave;nh thể xấu Răng thưa, răng h&ocirc; vẩu m&oacute;m</li><li>Răng mắc bệnh l&yacute; răng miệng như s&acirc;u răng, vi&ecirc;m tủy, m&ograve;n men răng.</li></ul><p><strong>Ưu điểm của răng sứ Zolid:</strong></p><ul><li><em>- Thẩm mỹ răng sứ đẹp tự nhi&ecirc;n, vượt trội </em></li><li><em>- Bền chắc, chịu lực lớn hơn cả răng thật </em></li><li><em>- L&agrave;nh t&iacute;nh, kh&ocirc;ng k&iacute;ch ứng răng miệng </em></li><li><em>- Tuổi thọ c&oacute; thể duy tr&igrave; l&acirc;u d&agrave;i l&ecirc;n tới 10-20 năm nếu được phục h&igrave;nh đ&uacute;ng kỹ thuật v&agrave; c&oacute; chế độ chăm s&oacute;c răng miệng tốt.</em></li></ul>', 1, 2, 1)
GO
INSERT [dbo].[tblServices] ([serviceID], [serviceName], [servicePrice], [description], [status], [adminID], [serviceTypeID]) VALUES (23, N'Răng sứ Emax', 3200000, N'<p><strong>Răng sứ Emax </strong>thuộc d&ograve;ng răng to&agrave;n sứ kh&ocirc;ng kim loại.</p><p>Cấu tạo của n&oacute; ho&agrave;n to&agrave;n được l&agrave;m từ <em><strong>gốm thủy tinh Lithium Disilicate.</strong></em></p><p><strong>Đặc biệt</strong>, <em><strong>sứ Emax c&oacute; kết cấu từ những tinh thể thủy tinh cực nhỏ.</strong></em></p><p>Ch&uacute;ng đan xen v&agrave;o nhau v&agrave; chĩa ra nhiều hướng, điều n&agrave;y khiến m&atilde;o sứ trở n&ecirc;n cứng c&aacute;p v&agrave; kh&oacute; bị nứt vỡ.</p><p>Hiện nay, <strong>răng to&agrave;n sứ Emax đang c&oacute; mặt tr&ecirc;n thị trường với 3 lo</strong>ại:</p><ul><li><em>Emax Press,</em></li><li><em>Emax Zirconia&nbsp;</em></li><li><em>Emax CAD.</em></li></ul><p><strong>Ưu điểm Răng sứ Emax:</strong></p><ul><li><em>- C&oacute; t&iacute;nh thẩm mỹ cao: D&ograve;ng sứ Emax c&oacute; độ phản quang, s&aacute;ng b&oacute;ng tốt v&agrave; kh&ocirc;ng bị đen viền nướu khi sử dụng l&acirc;u d&agrave;i. </em></li><li><em>- Mang lại cảm gi&aacute;c ăn nhai thoải m&aacute;i: Với khả năng chịu lực l&ecirc;n đến 850 Mpa. </em></li><li><em>- Răng sứ Emax c&oacute; tuổi thọ cao: Th&ocirc;ng thường, răng sứ c&oacute; thể tồn tại l&ecirc;n đến 20 năm trong khoang miệng. </em></li><li><em>- C&oacute; đến 16 tone m&agrave;u để thỏa sức lựa chọn sao cho ph&ugrave; hợp với khu&ocirc;n miệng, m&agrave;u da.</em></li></ul>', 1, 2, 1)
GO
INSERT [dbo].[tblServices] ([serviceID], [serviceName], [servicePrice], [description], [status], [adminID], [serviceTypeID]) VALUES (24, N'Răng sứ Cercon', 6000000, N'<p><strong>Răng sứ Cercon</strong> l&agrave; loại răng sứ c&oacute; nguồn gốc từ Đức v&agrave; được chế t&aacute;c theo<em><strong> c&ocirc;ng nghệ CAD/CAM hiện đại nhất hiện nay.</strong></em></p><p><em><strong>Đ&acirc;y l&agrave; một trong những d&ograve;ng răng sứ to&agrave;n sứ cao cấp nhất</strong></em>, khắc phục ho&agrave;n to&agrave;n những khuyết điểm của răng sứ kim loại như:</p><ul><li>m&agrave;u sắc trắng trong tự nhi&ecirc;n,</li><li>kh&ocirc;ng đổi m&agrave;u răng theo thời gian hay kh&ocirc;ng bị đen viền nướu&hellip;</li></ul><p>Cũng giống như c&aacute;c loại răng to&agrave;n sứ kh&aacute;c, <strong>răng sứ Cercon với cấu tr&uacute;c gồm 2 phần:</strong></p><ul><li>- B&ecirc;n trong: L&agrave; khung sườn l&agrave;m từ vật liệu sứ</li><li>- B&ecirc;n ngo&agrave;i: Được phủ nhiều lớp sứ, gi&uacute;p răng đạt đến tr&igrave;nh độ trắng s&aacute;ng tự nhi&ecirc;n như răng thật.</li></ul><p><strong>Răng sứ Cercon mang cấu tr&uacute;c của răng sứ Cercon v&agrave; những đặc điểm nổi bật như:</strong></p><ul><li><em>Răng sứ cứng chắc, với khả năng chịu lực tốt v&agrave; độ bền cao </em></li><li><em>Răng sứ Cercon l&agrave; răng to&agrave;n sứ n&ecirc;n rất an to&agrave;n, l&agrave;nh t&iacute;nh dễ tương th&iacute;ch với c&aacute;c m&ocirc; mềm do đ&oacute; ho&agrave;n to&agrave;n kh&ocirc;ng g&acirc;y k&iacute;ch ứng m&ocirc; nướu.</em></li><li><em>Kh&oacute; bị m&agrave;i m&ograve;n trong m&ocirc;i trường miệng Kh&ocirc;ng bị đen viền nướu sau 1 thời gian sử dụng như răng sứ kim loại</em></li></ul>', 1, 2, 1)
