INSERT [dbo].[tblServices] ([serviceID], [serviceName], [servicePrice], [description], [status], [adminID], [serviceTypeID]) VALUES (25, N'Răng sứ Chrome Cobalt', 3000000, N'<p><em><strong>Răng sứ Chrome Cobalt hay răng sứ Co-Cr</strong></em> thuộc d&ograve;ng răng sứ kim loại, với th&agrave;nh phần cấu tạo ch&iacute;nh l&agrave; từ <em><strong>hợp kim Chromium</strong></em> v&agrave; <strong><em>Cobalt kh&ocirc;ng chứa Niken.</em></strong></p><p>Lớp sườn b&ecirc;n trong được l&agrave;m bằng <em><strong>hợp kim Co-Cr</strong></em>, c&oacute; thể được phủ một lớp sứ trắng s&aacute;ng ra b&ecirc;n ngo&agrave;i hoặc kh&ocirc;ng.</p><p>Lớp sứ trắng s&aacute;ng b&ecirc;n ngo&agrave;i của <em><strong>răng Chrome Cobalt</strong></em> đảm bảo t&iacute;nh <em><strong>thẩm mỹ tương đồng với răng thật.</strong></em></p><p>N&ecirc;n sau khi phục h&igrave;nh với răng sứ, răng thật được khắc phục về mặt h&igrave;nh thể, thẩm mỹ, mang lại vẻ đẹp ban đầu của răng.</p><p><strong>Răng sứ Chrome Cobalt</strong> c&oacute; lớp sườn kim loại <em><strong>cứng chắc</strong></em>, được nung ở nhiệt độ cao n&ecirc;n đảm bảo tốt về<em><strong> khả năng ăn nhai</strong></em>, <em><strong>khả năng chịu lực lớn, cứng chắc.</strong></em></p><p><strong>Răng sứ kim loại cr-co c&oacute; tốt kh&ocirc;ng?</strong></p><ul><li><em>- T&iacute;nh ứng dụng cao, &aacute;p dụng cho nhiều trường hợp phục h&igrave;nh. </em></li><li><em>- T&aacute;i tạo h&igrave;nh thể, cải thiện thẩm mỹ v&agrave; ăn nhai cho răng. </em></li><li><em>- An to&agrave;n, l&agrave;nh t&iacute;nh, kh&ocirc;ng g&acirc;y k&iacute;ch ứng răng miệng.</em></li></ul>', 1, 2, 1)
GO
INSERT [dbo].[tblServices] ([serviceID], [serviceName], [servicePrice], [description], [status], [adminID], [serviceTypeID]) VALUES (26, N'Đắp tạo kiểu răng khểnh', 700000, N'<p><strong>Đắp răng khểnh</strong> l&agrave; một trong những phương ph&aacute;p được nhiều người sử dụng để tạo ra <em><strong>chiếc răng khểnh giả ở vị tr&iacute; răng số 3 (răng nanh).</strong></em></p><p>Phương ph&aacute;p n&agrave;y sử dụng <strong><em>chất liệu nh&acirc;n tạo</em></strong> để đắp l&ecirc;n tr&ecirc;n bề mặt răng thật, tạo h&igrave;nh cho giống với chiếc răng khểnh.</p><p><strong>Quy tr&igrave;nh cụ thể như sau:</strong></p><ul><li><em>- Bước 1: Vệ sinh khoang miệng v&agrave; vị tr&iacute; răng cần đắp </em></li><li><em>- Bước 2: Đắp Composite l&ecirc;n răng nanh số 3 v&agrave; tạo h&igrave;nh sao cho chiếc răng khểnh giả đạt t&iacute;nh thẩm mỹ ho&agrave;n thiện </em></li><li><em>- Bước 3: Chiếu đ&egrave;n Laser để h&oacute;a cứng Composite.</em></li></ul><p><strong>Ưu điểm:</strong></p><ul><li><em>- Thời gian thực hiện nhanh ch&oacute;ng trong v&ograve;ng<strong> 20 - 30 ph&uacute;t </strong></em></li><li><em>- T&iacute;nh thẩm mỹ cao, răng c&oacute; độ trắng s&aacute;ng <strong>giống với răng thật </strong></em></li><li><em>- Quy tr&igrave;nh thực hiện <strong>đơn giản, nhanh ch&oacute;ng</strong> v&agrave; kh&ocirc;ng t&aacute;c động hay l&agrave;m tổn thương đến c&aacute;c m&ocirc; mềm </em></li><li><em>- Chi ph&iacute; rẻ</em></li><li><em>- C&oacute; thể th&aacute;o Composite nếu kh&ocirc;ng c&ograve;n th&iacute;ch th&uacute; với răng khểnh</em></li></ul>', 1, 2, 7)
GO
INSERT [dbo].[tblServices] ([serviceID], [serviceName], [servicePrice], [description], [status], [adminID], [serviceTypeID]) VALUES (27, N'Hàm trainer trẻ em ', 1500000, N'<p><strong>H&agrave;m trainer</strong> l&agrave; c&ocirc;ng cụ chỉnh nha chủ yếu d&agrave;nh cho trẻ em <em><strong>từ 2 đến 15 tuổi.</strong></em></p><p>Việc sử dụng c&aacute;c <em><strong>h&agrave;m trainer c&oacute; t&aacute;c dụng ngăn ngừa t&igrave;nh trạng sai khớp cắn</strong></em>, răng mọc lệch trong giai đoạn ph&aacute;t triển ở trẻ em.</p><p><em><strong>H&agrave;m trainer c&ograve;n được gọi l&agrave; niềng răng silicon</strong></em>, l&agrave; một trong những phương ph&aacute;p niềng răng phổ biến hiện nay được <strong><em>thiết kế dạng h&igrave;nh parabol &ocirc;m s&aacute;t</em></strong> v&agrave;o cung răng tự nhi&ecirc;n,<em><strong> kh&ocirc;ng c&oacute; mắc c&agrave;i hay d&acirc;y cung g&acirc;y vướng v&iacute;u cho người sử dụng.</strong></em></p><p>Trường hợp n&ecirc;n sử dụng h&agrave;m trainer: Phần lớn trẻ em đều dễ gặp phải vấn đề răng kh&ocirc;ng đều, r&otilde; rệt nhất trong khoảng từ 3 đến 5 tuổi.</p><p><strong>C&aacute;c trường hợp n&ecirc;n được c&acirc;n nhắc tiền chỉnh nha n&oacute;i chung, sử dụng h&agrave;m trainer n&oacute;i ri&ecirc;ng bao gồm:</strong></p><ul><li><em>- Răng qu&aacute; thưa </em></li><li><em>- Răng chen ch&uacute;c </em></li><li><em>- Răng kh&ocirc;ng đều </em></li><li><em>- Khớp cắn s&acirc;u (răng h&ocirc;) </em></li><li><em>-&nbsp; Răng h&agrave;m tr&ecirc;n ch&igrave;a ra ngo&agrave;i h&agrave;m dưới.</em></li></ul><p><strong>Bạn cần lưu &yacute; một số vấn đề khi sử dụng h&agrave;m trainer bao gồm:</strong></p><p><em><strong>Ch&uacute; &yacute; th&oacute;i quen xấu của trẻ</strong></em>, <em>vệ sinh h&agrave;m trainer thường xuy&ecirc;n bằng nước sạch</em> hoặc <em>ng&acirc;m v&agrave;o nước muối nhạt, đồng thời <strong>bảo quản nơi tho&aacute;ng m&aacute;t kh&ocirc; r&aacute;o</strong></em></p>', 1, 2, 2)
GO
INSERT [dbo].[tblServices] ([serviceID], [serviceName], [servicePrice], [description], [status], [adminID], [serviceTypeID]) VALUES (33, N'Nhổ răng khôn mọc ngầm', 2000000, N'<p>Trong tất cả răng kh&ocirc;n, th&igrave; <strong>răng kh&ocirc;n mọc ngầm</strong> được coi l&agrave;<strong><em> phưc tạp nhất.</em></strong></p><p>Bới v&igrave; ch&uacute;ng <em><strong>mắc kẹt trong nứu</strong></em>, kh&ocirc;ng thể quan s&aacute;t bằng mắt thường.</p><ul><li>Ngo&agrave;i việc mọc ngầm, <em>răng kh&ocirc;n c&ograve;n c&oacute; thể mọc thoe nhiều kiểu kh&aacute;c nhau</em> như<em> mọc nghi&ecirc;ng, mọc ngược, mọc kẹt.</em></li></ul><p><em><strong>Răng kh&ocirc;n mọc ngầm</strong></em> trong xương h&agrave;m hoặc dưới nướu <em><strong>g&acirc;y đau nhức, &ecirc; buốt dữ dội.</strong></em></p><p><strong>V&igrave; sao n&ecirc;n nhổ răng kh&ocirc;n mọc ngầm ?</strong></p><ul><li><em>- Vi&ecirc;m nứu, L&agrave;m tổn thương răng số 7, U nang xương h&agrave;m, rối loạn về phản xạ v&agrave; cảm gi&aacute;c </em></li><li><em>- Răng kh&ocirc;n mọc ngầm, mọc lệch g&acirc;y đau đớn v&agrave; ảnh hưởng đến sức khỏe ch&uacute;ng ta.</em></li></ul><p>Ch&iacute;nh v&igrave; vậy c&aacute;c b&aacute;c sĩ thường khuy&ecirc;n n&ecirc;n nhổ c&agrave;ng sớm c&agrave;ng tốt để tr&aacute;nh những biến chứng c&oacute; thể xảy ra.</p><p><strong>Tuy nhi&ecirc;n với những trường hợp răng kh&ocirc;n mọc thẳng c&oacute; thể thực hiện thủ thuật nhổ bỏ hoặc kh&ocirc;ng ?</strong></p><p>C&aacute;c răng kh&ocirc;n mọc thẳng vẫn g&acirc;y đau đớn cho bệnh nh&acirc;n nhưng <strong><em>kh&ocirc;ng n&ecirc;n nhổ bỏ</em></strong> nếu bệnh nh&acirc;n mắc c&aacute;c bệnh l&yacute; như: <em><strong>Tim mạch; Đ&aacute;i th&aacute;o đường; Rối loạn đ&ocirc;ng cầm m&aacute;u.</strong></em></p>', 1, 2, 4)
GO
INSERT [dbo].[tblServices] ([serviceID], [serviceName], [servicePrice], [description], [status], [adminID], [serviceTypeID]) VALUES (34, N'Nhổ răng khôn mọc thẳng', 1000000, N'<p>Tr&ecirc;n thực tế, việc <em><strong>mọc răng kh&ocirc;n</strong></em> kh&ocirc;ng mang lại nhiều lợi &iacute;ch m&agrave; <em>chủ yếu mang lại nhiều bất lợi</em> v&agrave; <em>phiền to&aacute;i cho răng miệng.</em></p><p>Do <strong><em>răng kh&ocirc;n mọc kh&ocirc;ng thẳng h&agrave;ng</em></strong> v&agrave; mọc ngầm n&ecirc;n b&aacute;c sĩ thường chỉ định nhổ răng để ngăn ngừa những biến chứng do ch&uacute;ng g&acirc;y ra.</p><p>Ngược lại, <em><strong>khi răng kh&ocirc;n mọc d&agrave;i v&agrave; thẳng</strong></em>, ch&uacute;ng c&oacute; thể mọc l&ecirc;n b&igrave;nh thường v&agrave; <em>c&oacute; thể giữ lại miễn l&agrave; kh&ocirc;ng ảnh hưởng đến c&aacute;c răng kế cận.</em></p><p>Tuy nhi&ecirc;n, <em><strong>trong một số trường hợp, răng kh&ocirc;n mọc thẳng cần được xử l&iacute;: </strong></em></p><ul><li><em>Nếu kh&ocirc;ng nhổ, những chiếc răng n&agrave;y sẽ ch&igrave;a ra h&agrave;m dưới đối diện, <strong>g&acirc;y vướng v&iacute;u, kh&oacute; ăn nhai v&agrave; lệch h&agrave;m, thậm ch&iacute; sưng tấy, vi&ecirc;m nhiễm do răng thường xuy&ecirc;n bị ma s&aacute;t.</strong></em></li><li><em>Răng kh&ocirc;n c&oacute; k&iacute;ch thước qu&aacute; lớn, qu&aacute; d&agrave;i, qu&aacute; nhỏ, <strong>ch&egrave;n &eacute;p c&aacute;c răng kh&aacute;c</strong>, tạo khoảng trống với răng số 7, c&oacute; thể khiến thức ăn bị mắc kẹt.</em></li><li><em>Nếu vệ sinh kh&ocirc;ng kỹ sẽ g&acirc;y ra c&aacute;c bệnh l&yacute; răng miệng như s&acirc;u răng, vi&ecirc;m nha chu.</em></li></ul><p>Ngo&agrave;i ra, răng kh&ocirc;n qu&aacute; lớn c&oacute; thể g&acirc;y ra t&igrave;nh trạng rụng căng, vướng v&ugrave;ng lợi; khiến người bệnh kh&oacute; ăn, nhai.</p><p><strong><em>Khắc phục t&igrave;nh trạng đau nhức</em></strong>, kh&oacute; chịu v&agrave; hạn chế khả năng ăn nhai, v&igrave; răng kh&ocirc;n d&ugrave; mọc b&igrave;nh thường, mọc thẳng cũng bị nướu che mất.</p>', 1, 2, 4)
GO
INSERT [dbo].[tblServices] ([serviceID], [serviceName], [servicePrice], [description], [status], [adminID], [serviceTypeID]) VALUES (35, N'Nhổ răng khôn mọc lệch 90 độ', 1500000, N'<p><strong>Răng kh&ocirc;n mọc lệch 90 độ</strong> l&agrave; trường hợp kh&aacute; phức tạp v&agrave; dẫn đến <strong>nhiều hệ lụy cho sức khỏe.</strong></p><p><strong>Răng kh&ocirc;n 90 đ</strong>ộ l&agrave; khi răng mọc l&ecirc;n <em><strong>đ&acirc;m trực tiếp v&agrave;o răng số 7</strong></em> thoe một g&oacute;c 90 độ.</p><p>Đầu ti&ecirc;n <strong>răng kh&ocirc;n mọc lệch 90 độ</strong> sẽ <em>t&aacute;c động trực tiếp đến răng số 7</em>, c&oacute; thể l&agrave;m hỏng g&acirc;y tổn thương lớn ở v&ugrave;ng xương h&agrave;m.</p><p><strong>Nếu kh&ocirc;ng loại bỏ kịp thời</strong> th&igrave;<em> răng số 7 sẽ mất dần chức năng</em> v&agrave; phải nhổ n&oacute;.</p><p><strong>Bạn cần nhổ răng ngay khi gặp một v&agrave;i triệu chứng như</strong></p><ul><li>- Thường xuy&ecirc;n phải chịu những cơn đau kh&oacute; chịu.</li><li>- Nứu răng bị sưng đỏ, v&agrave; c&oacute; thể c&oacute; mủ, c&oacute; m&ugrave;i h&ocirc;i miệng.</li><li>- Răng bị nhồi nh&eacute;t thức ăn, g&acirc;y s&acirc;u răng v&agrave; vi&ecirc;m nha chu răng số 7.</li></ul><p><strong>Nhổ răng kh&ocirc;n mọc lẹch 90 độ c&oacute; kh&oacute; kh&ocirc;ng?</strong></p><p><strong>Độ kh&oacute; của ca nhổ</strong> răng kh&ocirc;n sẽ phụ thuộc v&agrave;o <strong>hướng ch&acirc;n răng, độ s&acirc;u, k&iacute;ch thước...</strong></p><ul><li><em>Đố với răng kh&ocirc;n mọc lệch 90 độ, ca nhổ sẽ kh&oacute; khăn hơn v&agrave; cần nhiều thời gian phẫu thuật hơn. </em></li><li><em>Bởi răng mọc ngầm dưới lợi n&ecirc;n cần giải phẫu thần kinh ống răng dưới, xoang h&agrave;m tr&ecirc;n...</em></li><li><em>Với trang thiết bị hiện đại, bạn kh&ocirc;ng cần qu&aacute; lo lắng, việc nhổ răng kh&ocirc;n mọc lệch 90 độ sẽ diễn ra rất an to&agrave;n v&agrave; kh&ocirc;ng g&acirc;y nguy hiểm. Mỗi ca mổ sẽ diễn ra từ 15-30 ph&uacute;t</em></li></ul>', 1, 2, 4)
