INSERT [dbo].[tblServices] ([serviceID], [serviceName], [servicePrice], [description], [status], [adminID], [serviceTypeID]) VALUES (39, N'Nhổ răng khôn mọc lệch 45 độ', 1200000, N'<p>Nếu h&agrave;m thiếu khoảng mọc răng th&igrave; sẽ khiến răng kh&ocirc;n c&oacute; xu hướng mọc chen ch&uacute;c, <strong><em>nguy cơ cao răng kh&ocirc;n mọc lệch 45 độ,</em></strong> <strong><em>nghi&ecirc;m trọng hơn l&agrave; mọc lệch 90 độ.</em></strong></p><p><em><strong>Răng kh&ocirc;n mọc lệch 45 độ</strong></em> tồn tại tr&ecirc;n cung h&agrave;m, theo thời gian sẽ l&agrave;m ảnh hưởng nghi&ecirc;m trọng đến chất lượng cuộc sống v&agrave; g&acirc;y ra c&aacute;c biến chứng nguy hiểm như sau:</p><ul><li><em>- Răng kh&ocirc;n mọc lệch về ph&iacute;a răng số 7 sẽ l&agrave;m chiếc răng n&agrave;y bị x&ocirc; đẩy theo </em></li><li><em>- H&igrave;nh th&agrave;nh ổ vi&ecirc;m nhiễm, vi&ecirc;m lợi tr&ugrave;m răng kh&ocirc;n, tiến triển nặng hơn sẽ g&acirc;y &aacute;p xe răng v&agrave; vi&ecirc;m quanh răng rất nguy hiểm.</em></li><li><em>- T&igrave;nh trạng vi&ecirc;m nhiễm l&acirc;u ng&agrave;y do răng kh&ocirc;n mọc lệch 45 độ c&oacute; nguy cơ l&agrave;m vi&ecirc;m cơ h&agrave;m, rối loạn khớp th&aacute;i dương h&agrave;, tổn thương d&acirc;y thần kinh nếu ch&acirc;n răng mọc ch&egrave;n l&ecirc;n d&acirc;y thần kinh.</em></li></ul><p><strong>Trang thiết bị trong qu&aacute; tr&igrave;nh nhổ răng sẽ t&aacute;c động đến việc nhổ răng kh&ocirc;n c&oacute; đau kh&ocirc;ng?</strong></p><p>Với đầy đủ c&aacute;c <em><strong>trang thiết bị tối t&acirc;n v&agrave; hiện đại</strong></em> tại Nha Khoa m&aacute;y cắt răng, dao điện, m&aacute;y Piezotome, m&aacute;y BienAir,&hellip; th&igrave; sẽ <em><strong>gi&uacute;p nhổ răng kh&ocirc;n nhanh ch&oacute;ng, tr&aacute;nh c&aacute;c sang chấn sau nhổ.</strong></em></p>', 1, 2, 4)
GO
INSERT [dbo].[tblServices] ([serviceID], [serviceName], [servicePrice], [description], [status], [adminID], [serviceTypeID]) VALUES (41, N'Mắc cài tự khóa thông minh', 32000000, N'<p><em><strong>Mắc c&agrave;i kim loại th&ocirc;ng minh </strong>(</em>tự buộc/tự kh&oacute;a) về bản chất vẫn l&agrave; phương ph&aacute;p chỉnh nha sử dụng hệ thống mắc c&agrave;i v&agrave; d&acirc;y cung để tạo lực siết cho răng.</p><p><em><strong>Điểm cải tiến</strong></em> của phương ph&aacute;p n&agrave;y l&agrave;<em><strong> kh&ocirc;ng cần thun mắc c&agrave;i</strong></em> v&igrave; đ&atilde; c&oacute; hệ thống nắp trượt tự động, <em>gi&uacute;p d&acirc;y cung trượt tự do ở tr&ecirc;n r&atilde;nh của mắc c&agrave;i.</em></p><p><em>Nhờ vậy gi&uacute;p răng dịch chuyển li&ecirc;n tục</em> v&agrave; nhờ vậy qu&aacute; tr&igrave;nh niềng răng cũng trở n&ecirc;n <em><strong>thuận tiện v&agrave; r&uacute;t ngắn thời gian hơn.</strong></em></p><p><strong>C&aacute;c trường hợp c&oacute; thể &aacute;p dụng phương ph&aacute;p niềng răng mắc c&agrave;i kim loại tự động gồm</strong></p><ul><li><em>- Răng mọc thưa, răng mọc chen ch&uacute;c, răng kh&ocirc;ng đều </em></li><li><em>- Bị sau khớp cắn - Răng m&oacute;m, răng h&ocirc; </em></li><li><em>- Răng mọc ngược, răng khểnh hoặc mọc lệch.</em></li></ul><p><strong>Ưu điểm của mắc c&agrave;i kim loại tự động:</strong></p><ul><li><em>-Chi ph&iacute; hợp t&uacute;i tiền </em></li><li><em>- Thời gian niềng nhanh hơn </em></li><li><em>- Hiệu quả niềng răng cao </em></li><li><em>- Vệ sinh răng miệng dễ d&agrave;ng hơn </em></li><li><em>- Kh&ocirc;ng bị bung tuột</em></li></ul>', 1, 2, 2)
GO
INSERT [dbo].[tblServices] ([serviceID], [serviceName], [servicePrice], [description], [status], [adminID], [serviceTypeID]) VALUES (43, N'Mắc cài sứ, pha lê thẩm mỹ', 38000000, N'<p><strong>Niềng răng mắc c&agrave;i sứ</strong> l&agrave; phương ph&aacute;p chỉnh nha &aacute;p dụng kh&iacute; cụ chỉnh nha tương tự niềng răng mắc c&agrave;i kim loại,</p><p><strong>Điểm kh&aacute;c biệt</strong> l&agrave; mắc c&agrave;i kim loại được thay bằng mắc c&agrave;i <strong><em>chất liệu sứ.</em></strong></p><p><strong>Niềng răng mắc c&agrave;i sứ bao gồm:</strong></p><ul><li><em>Mắc c&agrave;i chất liệu sứ gắn cố định tr&ecirc;n bề mặt răng, </em></li><li><em>D&acirc;y cung nằm trong r&atilde;nh mắc c&agrave;i.</em></li></ul><p><strong>Mắc c&agrave;i sứ</strong> c&oacute; m&agrave;u trắng trong rất giống răng thật n&ecirc;n sẽ <em><strong>&iacute;t bị lộ niềng hơn</strong></em> phương ph&aacute;p niềng răng mắc c&agrave;i truyền thống.</p><p><strong>Đối tượng sử dụng niềng răng mắc c&agrave;i sứ thẩm mỹ:</strong></p><ul><li><em>- Răng khấp khểnh, răng thưa, răng h&ocirc; vẩu, răng m&oacute;m... </em></li><li><em>- Răng bị sai lệch khớp</em></li><li><em>- C&aacute;c trường hợp li&ecirc;n quan đến h&agrave;m như m&eacute;o h&agrave;m, h&agrave;m hẹp, h&agrave;m lệch. Ưu điểm của niềng răng mắc c&agrave;i sứ: </em></li><li><em>- T&iacute;nh thẩm mỹ cao. </em></li><li><em>- Chất liệu an to&agrave;n th&acirc;n thiện với người sử dụng</em></li><li><em>- Cảm gi&aacute;c thoải m&aacute;i kh&ocirc;ng g&acirc;y đau nhức, trơn l&aacute;ng kh&ocirc;ng g&acirc;y tổn thương , gi&uacute;p vệ sinh răng miệng tốt </em></li><li><em>- Vật liệu sứ l&agrave;m niềng răng c&oacute; khả năng chịu lực cao</em></li></ul>', 1, 2, 2)
GO
INSERT [dbo].[tblServices] ([serviceID], [serviceName], [servicePrice], [description], [status], [adminID], [serviceTypeID]) VALUES (46, N'Tẩy trắng răng tại phòng mạch bằng đèn Laser Whitening', 2500000, N'<p><strong>C&ocirc;ng nghệ tẩy trắng răng bằng đ&egrave;n Laser v&agrave; đ&egrave;n Zoom</strong> đều c&oacute; một cơ chế kỹ thuật chung l&agrave; <em>sử dụng &aacute;nh đ&egrave;n để hoạt h&oacute;a</em> v&agrave;o <em>thuốc tẩy tr&ecirc;n bề mặt men răng</em> để tạo th&agrave;nh một loại phản ứng oxy h&oacute;a khử ph&acirc;n t&aacute;ch chuỗi protein m&agrave;u b&aacute;m tr&ecirc;n răng, gi&uacute;p đ&aacute;nh bật ch&uacute;ng ra khỏi răng v&agrave; <strong><em>l&agrave;m cho men răng trắng s&aacute;ng dần l&ecirc;n</em></strong>.</p><p><em><strong>Th&ocirc;ng thường, c&aacute;c phương ph&aacute;p tẩy trắng răng bằng c&ocirc;ng nghệ đ&egrave;n sẽ được &aacute;p dụng trong c&aacute;c trường hợp sau đ&acirc;y:</strong></em></p><p><em>&ndash; Trường hợp răng bị ố m&agrave;u, ng&atilde; v&agrave;ng do sử dụng những loại thực phẩm c&oacute; chứa nhiều m&agrave;u sắc trong thời gian d&agrave;i. </em></p><p><em>&ndash; T&igrave;nh trạng vệ sinh răng kh&ocirc;ng sạch khiến cho c&aacute;c mảng b&aacute;m c&ograve;n ứ đọng trong răng. </em></p><p><em>&ndash; Răng bị nhiễm m&agrave;u kh&aacute;ng sinh hoặc răng bị thiểu sản men, nhiễm m&agrave;u Fluor ở dạng nhẹ v&agrave; b&aacute;c sĩ cho rằng c&oacute; thể tẩy trắng được&hellip;</em></p><p><em><strong>M&agrave;u trắng của răng</strong></em> sau khi tẩy c&oacute; thể duy tr&igrave; được l&acirc;u hay nhanh đổi m&agrave;u phần lớn đều <strong><em>phụ thuộc v&agrave;o c&aacute;ch chăm s&oacute;c, vệ sinh răng miệng</em></strong> của ch&iacute;nh bản th&acirc;n bệnh nh&acirc;n.</p><p><strong><em>Thời gian duy tr&igrave; m&agrave;u trắng của răng</em></strong> khi tẩy bằng đ&egrave;n Zoom sẽ được đảm bảo nhỉnh hơn phương ph&aacute;p tẩy trắng răng bằng Laser khoảng<strong><em> 1 - 2 năm.</em></strong></p>', 1, 2, 7)
GO
INSERT [dbo].[tblServices] ([serviceID], [serviceName], [servicePrice], [description], [status], [adminID], [serviceTypeID]) VALUES (48, N'Lấy cao răng cho trẻ em', 150000, N'<p><strong>Lấy cao răng</strong> l&agrave; việc l&agrave;m cần được thực hiện định kỳ nhằm <em><strong>loại bỏ cao răng</strong></em> v&agrave; <strong><em>c&aacute;c mảng b&aacute;m tr&ecirc;n răng</em></strong> gi&uacute;p răng miệng sạch sẽ.</p><p><em><strong>Đối với trẻ em</strong></em> cũng cần được thực hiện lấy cao răng để<strong><em> tr&aacute;nh những t&aacute;c hại xấu</em></strong> do cao răng g&acirc;y ra.&nbsp;</p><p><strong>C&oacute; n&ecirc;n lấy cao răng cho trẻ?</strong></p><p><strong>Cao răng</strong> c&oacute; thể dẫn đến c&aacute;c bệnh như vi&ecirc;m lợi với c&aacute;c biểu hiện đ&aacute;nh răng chảy m&aacute;u, miệng c&oacute; m&ugrave;i h&ocirc;i ở trẻ.</p><p><strong>Cao răng</strong> cũng c&oacute; thể <strong><em>g&acirc;y vi&ecirc;m nha chu</em></strong> dẫn đến ti&ecirc;u xương l&agrave;m <em>răng bị đau, &ecirc; buốt khi ăn nhai v&agrave; đau nhức, dẫn tới t&igrave;nh trạng trẻ k&eacute;m ăn, mất ngủ,...</em></p><p>Thậm ch&iacute;, <strong><em>trường hợp nặng hơn</em></strong> c&oacute; thể g&acirc;y lung lay v&agrave; rụng răng.</p><p><strong><em>Lấy cao răng</em></strong> l&agrave; một kỹ thuật nha khoa đơn giản, c&oacute; thể thực hiện <strong><em>nhanh ch&oacute;ng, kh&ocirc;ng cần g&acirc;y t&ecirc; hay sử dụng bất kỳ loại thuốc n&agrave;o.</em></strong></p><p>Vậy n&ecirc;n, <strong><em>lấy cao răng</em></strong> l&agrave; việc l&agrave;m <em><strong>cần được thực hiện định kỳ</strong></em> nhằm loại bỏ cao răng v&agrave; c&aacute;c mảng b&aacute;m tr&ecirc;n răng gi&uacute;p răng miệng sạch sẽ.</p>', 1, 2, 6)
GO
INSERT [dbo].[tblServices] ([serviceID], [serviceName], [servicePrice], [description], [status], [adminID], [serviceTypeID]) VALUES (50, N'Phẫu thuật cắt nướu, tạo hình nướu, làm dài thân răng', 1000000, N'<p><strong>L&agrave;m d&agrave;i th&acirc;n răng</strong> l&agrave; kĩ thuật nha khoa kh&aacute; đơn giản v&agrave; thường kh&ocirc;ng tốn nhiều thời gian. Hiểu đơn giản l&agrave; b&aacute;c sĩ sẽ <em>phẫu thuật cắt bỏ một phần m&ocirc; nướu hoặc xương.</em></p><p>Hoặc c&oacute; trường hợp phải xử l&yacute; loại bỏ cả hai.</p><p>Thủ thuật nhỏ n&agrave;y <strong><em>gi&uacute;p l&agrave;m lộ th&acirc;n răng ra nhiều hơn</em></strong> khiến tỉ lệ răng v&agrave; nướu về mức c&acirc;n đối.</p><p>B&ecirc;n cạnh đ&oacute;, n&oacute; c&ograve;n <strong>l&agrave; phương ph&aacute;p tiền đề</strong> phục vụ cho c&aacute;c kỹ thuật nha khoa kh&aacute;c. Mặc d&ugrave; đ&acirc;y l&agrave; một kĩ thuật nha khoa đơn giản.</p><p><strong>Ưu điểm của làm dài th&acirc;n răng</strong></p><ul><li><em>L&agrave;m d&agrave;i th&acirc;n răng mang lại cho bệnh nh&acirc;n những lợi &iacute;ch cả về thẩm mỹ lẫn sức khỏe răng miệng. </em></li><li><em>Cải thiện t&igrave;nh trạng cười lộ nướu, mang lại cho bệnh nh&acirc;n sự tự tin trong giao tiếp.</em></li><li><em>Vệ sinh răng miệng dễ d&agrave;ng hơn, tr&aacute;nh được t&igrave;nh trạng vi&ecirc;m nướu lợi, chảy m&aacute;u ch&acirc;n răng.</em></li></ul>', 1, 2, 6)
GO
INSERT [dbo].[tblServices] ([serviceID], [serviceName], [servicePrice], [description], [status], [adminID], [serviceTypeID]) VALUES (54, N'Điều trị nha chu bằng Laser Light Walker hai hàm', 5200000, N'<p><strong>Điều trị với Laser LightWalker</strong> gi&uacute;p đảm bảo m&ocirc; xương rất dễ tổn thương quanh implant giữ được nguy&ecirc;n vẹn.</p><p><em><strong>Kh&ocirc;ng c&oacute; nguy cơ tổn thương</strong></em> do nhiệt cho m&ocirc; xương xung quanh v&agrave; kh&ocirc;ng g&acirc;y ra sự thay đổi lớn tr&ecirc;n bề mặt implant như c&aacute;c bước s&oacute;ng laser kh&aacute;c.</p><p><strong>Kh&ocirc;ng c&oacute; tổn thương cơ học</strong>, h&oacute;a học hay c&aacute;c dạng tổn thương kh&aacute;c trong qu&aacute; tr&igrave;nh điều trị.</p><p><strong>Laser Lightwalker</strong> gi&uacute;p qu&aacute; tr&igrave;nh cấy gh&eacute;p implant v&agrave;o xương h&agrave;m được <em>v&ocirc; tr&ugrave;ng tuyệt đối v&agrave; xương xung quanh implant được bảo vệ nguy&ecirc;n vẹn.</em></p><p><em>Nhờ đ&oacute;, qu&aacute; tr&igrave;nh t&iacute;ch hợp xương diễn ra nhanh hơn, bệnh nh&acirc;n sớm c&oacute; răng chắc chắn để ăn nhai.&nbsp;</em></p>', 1, 2, 6)
GO
